const inputItems = $input.all();
const outputItems = [];

for (const item of inputItems) {
  // Acessa a string bruta do campo 'output'
  const rawJsonString = item.json.output;

  if (rawJsonString && typeof rawJsonString === 'string') {
    let cleanedJsonString = rawJsonString;

    // Remove '```json\n' do início
    if (cleanedJsonString.startsWith('```json\n')) {
      cleanedJsonString = cleanedJsonString.substring('```json\n'.length);
    }
    // Remove '```' do final
    if (cleanedJsonString.endsWith('```')) {
      cleanedJsonString = cleanedJsonString.substring(0, cleanedJsonString.length - '```'.length);
    }

    try {
      // Tenta parsear a string JSON limpa
      const parsedJson = JSON.parse(cleanedJsonString);
      // Adiciona o JSON parseado a uma nova propriedade 'parsedOutput' no item
      item.json.parsedOutput = parsedJson;
    } catch (e) {
      // Em caso de erro ao parsear, adicione um objeto de erro para depuração
      console.error("Erro ao parsear JSON:", e.message, "String bruta:", rawJsonString);
      item.json.parsedOutput = {
        error: "Failed to parse JSON",
        details: e.message,
        rawString: rawJsonString,
        cleanedStringAttempt: cleanedJsonString
      };
    }
  } else {
    // Caso a entrada 'output' não seja uma string ou esteja vazia
    item.json.parsedOutput = { error: "Input 'output' was not a valid string or was empty" };
  }
  outputItems.push(item);
}

return outputItems;

